import React, { useState, useEffect } from "react";
import axios from "axios";
import styles from "../pageStyling/TransactionUpdate.module.css";

const TransactionUpdater = ({ transactionId, refreshTransactions }) => {
    const [transaction_name, setTransactionName] = useState("");
    const [transaction_type, setTransactionType] = useState("spending");
    const [amount, setAmount] = useState(0.0);
    const [error, setError] = useState(null);
    const [balances, setBalances] = useState([]);
    const [fromBalance, setFromBalance] = useState(0);
    const [toBalance, setToBalance] = useState(0);
    const [isOpen, setIsOpen] = useState(false);

    useEffect(() => {
        const fetchTransactionAndBalances = async () => {
            try {
                // Fetch the transaction details
                const transactionResponse = await axios.get("/api/getTransaction", {
                    params: { transaction_id: transactionId },
                });
                const transaction = transactionResponse.data;
                setTransactionName(transaction.transaction_name);
                setTransactionType(transaction.transaction_type);
                setAmount(transaction.amount);
                setFromBalance(transaction.balance_id);

                // Fetch all balances
                const balancesResponse = await axios.get("/api/getBalances", {
                    params: { user_id: transaction.user_id },
                });
                setBalances(balancesResponse.data);
            } catch (e) {
                console.error("Error fetching transaction or balances:", e);
                setError("Failed to load transaction or balances.");
            }
        };
        fetchTransactionAndBalances();
    }, [transactionId]);

    const updateTransaction = async (e) => {
        e.preventDefault();
        setError(null);

        if (!transaction_name.trim()) {
            setError("Transaction name is required.");
            return;
        }

        if (transaction_type === "transfer" && (!toBalance || toBalance === fromBalance)) {
            setError("Please select a valid target balance for the transfer.");
            return;
        }

        try {
            // Handle balance updates
            await axios.put("/api/updateTransactionBalance", {
                transaction_type,
                balance_id: fromBalance,
                to_balance_id: toBalance,
                amount: parseFloat(amount),
            });

            // Update the transaction details
            await axios.put(`/api/updateTransactions/${transactionId}`, {
                transaction_name,
                transaction_type,
                amount,
            });

            refreshTransactions();
            setIsOpen(false);
        } catch (e) {
            console.error("Error updating transaction:", e);
            setError("An error occurred while updating the transaction.");
        }
    };

    return (
        <div className={styles.container}>
            <button onClick={() => setIsOpen(!isOpen)} className={styles.updateButton}>
                Update Transaction
            </button>
            {isOpen && (
                <form onSubmit={updateTransaction} className={styles.updateBox}>
                    <h2>Transaction Name</h2>
                    <input
                        type="text"
                        value={transaction_name}
                        onChange={(ev) => setTransactionName(ev.target.value)}
                        placeholder="Groceries"
                        required
                    />
                    <h2>Transaction Type</h2>
                    <select
                        value={transaction_type}
                        onChange={(ev) => setTransactionType(ev.target.value)}
                        required
                    >
                        <option value="spending">Spending</option>
                        <option value="income">Income</option>
                        <option value="transfer">Transfer</option>
                        <option value="pay credit">Pay Credit</option>
                    </select>
                    {transaction_type === "transfer" && (
                        <>
                            <h2>From Balance</h2>
                            <select
                                value={fromBalance}
                                onChange={(ev) => setFromBalance(Number(ev.target.value))}
                                required
                            >
                                {balances.map((balance) => (
                                    <option key={balance.balance_id} value={balance.balance_id}>
                                        {balance.balance_name}
                                    </option>
                                ))}
                            </select>
                            <h2>To Balance</h2>
                            <select
                                value={toBalance}
                                onChange={(ev) => setToBalance(Number(ev.target.value))}
                                required
                            >
                                <option value="">Select Balance</option>
                                {balances
                                    .filter((balance) => balance.balance_id !== fromBalance)
                                    .map((balance) => (
                                        <option key={balance.balance_id} value={balance.balance_id}>
                                            {balance.balance_name}
                                        </option>
                                    ))}
                            </select>
                        </>
                    )}
                    <h2>Amount</h2>
                    <input
                        type="number"
                        value={amount}
                        onChange={(ev) => setAmount(parseFloat(ev.target.value))}
                        required
                    />
                    <button type="submit" className={styles.updateButton}>
                        Update
                    </button>
                    <button onClick={() => setIsOpen(false)} className={styles.cancelButton}>
                        Cancel
                    </button>
                    {error && <p className={styles.error}>{error}</p>}
                </form>
            )}
        </div>
    );
};

export default TransactionUpdater;
