import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { MyContext } from "../MyContext";
import styles from "../pageStyling/TransactionManager.module.css";

const TransactionManager = () => {
    const { user_id } = useContext(MyContext);
    const [transactions, setTransactions] = useState([]);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        if (!user_id) {
            setError("User ID is not defined.");
            return;
        }

        axios
            .get("/api/getTransactions", { params: { user_id } })
            .then((response) => {
                setTransactions(response.data);
            })
            .catch((error) => {
                setError(error.message);
            });
    }, [user_id]);

    const handleDelete = (id) => {
        axios
            .delete(`/api/getTransactions/${id}`)
            .then(() => {
                setTransactions(
                    transactions.filter(
                        (transaction) => transaction.transaction_id !== id
                    )
                );
            })
            .catch((error) => {
                setError(error.message);
            });
    };

    const handleUpdate = (id) => {
        navigate(`/update-transaction/${id}`);
    };

    return (
        <div className={styles.transactionManager}>
            <h1 className={styles.title}>Transaction Manager</h1>
            {error && <p className={styles.error}>{error}</p>}
            {transactions.length > 0 ? (
                <ul className={styles.transactionList}>
                    {transactions.map((transaction) => (
                        <li
                            key={transaction.transaction_id}
                            className={styles.transactionItem}
                        >
                            <div className={styles.transactionInfo}>
                                <span>
                                    <strong>Name:</strong>{" "}
                                    {transaction.transaction_name}
                                </span>
                                <span>
                                    <strong>Type:</strong>{" "}
                                    {transaction.transaction_type}
                                </span>
                                <span>
                                    <strong>Amount:</strong> ${transaction.amount}
                                </span>
                            </div>
                            <div className={styles.transactionButtons}>
                                <button
                                    className={styles.deleteButton}
                                    onClick={() =>
                                        handleDelete(transaction.transaction_id)
                                    }
                                >
                                    Delete
                                </button>
                                <button
                                    className={styles.updateButton}
                                    onClick={() =>
                                        handleUpdate(transaction.transaction_id)
                                    }
                                >
                                    Update
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
            ) : (
                <p className={styles.noTransactions}>No transactions made yet.</p>
            )}
            <Link to="/newtransaction" className={styles.addButton}>
                Add Transaction
            </Link>
        </div>
    );
};

export default TransactionManager;
