import { React, useEffect, useState } from 'react';
import axios from "axios";


const Landing = () => {

    const username = useState("Jaime"); //For testing purposes, username will come from login
    const user_id = useState("1"); // Again, for testing, will be assigned after login and persist through app with cookies
    const [balances, setBalances] = useState([]);
    const [displayBalance, setDisplay] = useState(14);

    const [isOpen, setIsOpen] = useState(false);

    const toggleDropdown = () => {
        setIsOpen(!isOpen);
    };

    const selectorAction = (selection) => {
        setIsOpen(!isOpen);
        setDisplay(selection);
    };

    useEffect(() => {
        console.log("reached");
        const getBalances = async () => {
            try {
                const response = await axios.get('/api/getBalances', {
                    params: {
                        user_id: user_id
                    }
                });
                setBalances(response.data);
                setDisplay(response.data[0].balance_id)
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        // Call the async function
        getBalances();
    }, []);

    if (displayBalance !== null) {
        return (

            <div>
                <h1> User: </h1>
                <h2> Welcome {username} </h2>
                <h1> Balances: </h1>
                <div>
                    {balances.length === 0 && (
                        <div className='dropdown-menu'>
                            <button className='dropdown-button' onClick={toggleDropdown}>
                                Select Balance
                            </button>
                            {isOpen && (
                                <ul className='menu-list'>
                                    {balances.map((balance, index) => (
                                        <li className='menu-item'>
                                            <button onClick={() => selectorAction(index)}>
                                                {balance.balance_name}
                                            </button>
                                        </li>
                                    ))}
                                </ul>
                            )}
                        </div>
                    )}
                </div>
                <div>
                    {balances.length === 0 ? (
                        <h2> No Balances Available </h2>
                    ) : (
                        <div>
                            <h1> {balances[displayBalance].balance_name}:</h1>
                            <h2>${balances[displayBalance].balance_name}</h2>
                        </div>
                    )}
                </div>
            </div>
        );
    }
};

export default Landing;
