//List of transactions with buttons for deletion and updates
import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import styles from "../pageStyling/TransactionManager.module.css";

const TransactionManager = () => {
    const [transactions, setTransactions] = useState([]);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        axios.get("/api/getTransactions", { params: { user_id} }) // Replace '1' with dynamic user_id
            .then(response => {
                setTransactions(response.data);
            })
            .catch(error => {
                setError(error.message);
            });
    }, []);

    const handleDelete = (id) => {
        axios.delete(`/api/getTransactions/${id}`)
            .then(() => {
                setTransactions(transactions.filter(transaction => transaction.id !== id));
            })
            .catch(error => {
                setError(error.message);
            });
    };

    const handleUpdate = (id) => {
        navigate(`/update-transaction/${id}`);
    };

    return (
        <div className={styles.transactionManager}>
            <h1 className={styles.title}>Transaction Manager</h1>
            {error && <p className={styles.error}>{error}</p>}
            {transactions.length > 0 ? (
                <ul className={styles.transactionList}>
                    {transactions.map(transaction => (
                        <li key={transaction.transaction_id} className={styles.transactionItem}>
                            <span>{transaction.transaction_name}</span>
                            <span>{transaction.amount}</span>
                            <button
                                className={styles.deleteButton}
                                onClick={() => handleDelete(transaction.transaction_id)}
                            >
                                Delete
                            </button>
                            <button
                                className={styles.updateButton}
                                onClick={() => handleUpdate(transaction.transaction_id)}
                            >
                                Update
                            </button>
                        </li>
                    ))}
                </ul>
            ) : (
                <p className={styles.noTransactions}>No transactions made yet.</p>
            )}
            <Link to="/newtransaction" className={styles.addButton}>Add Transaction</Link>
        </div>
    );
};

export default TransactionManager;
