import { React, useContext, useEffect, useState } from 'react';
import { Link, Navigate } from 'react-router-dom';
import { MyContext } from '../MyContext';
import axios from "axios";


const BalanceManager = () => {

    const [balances, setBalances] = useState([]);
    const [updated, setUpdated] = useState(true);

    const { user_id, setUserID, validated, setValidated } = useContext(MyContext);

    const deleteBalance = async (selected) => {
        try {
            await axios.post('api/deleteTable', null, {
                params: {
                    balance_id: selected
                }
            });
            console.log('Table Successfully Deleted');
            setUpdated(true);
        }
        catch (e) {
            console.log("Error: ", e);
        }
    }

    function capitalizeFirstLetter(string) {
        return string.charAt(0).toUpperCase() + string.slice(1).toLowerCase();
    }

    useEffect(() => {
        const getBalances = async () => {
            try {
                const response = await axios.get('/api/getBalances', {
                    params: {
                        user_id: user_id
                    }
                });
                setBalances(response.data);
                setUpdated(false);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        // Call the async function
        getBalances();
    }, [updated]);

    const logout = () => {
        setUserID(null);
        setValidated(false);
    }

    if (!validated) {
        return <Navigate to={'/login'} />
    }
    return (
        <div>
            <h1 className="title">List of Balances</h1>
            <div className="balanceBox balance-list">
                {balances.map((balance) => (
                    <div key={balance.balance_id} className="balance-item">
                        <div className="balance-info">
                            <h2>{balance.balance_name}</h2>
                            <h3>{capitalizeFirstLetter(balance.balance_type)}: ${balance.amount}</h3>
                        </div>
                        <button onClick={() => deleteBalance(balance.balance_id)}>Delete</button>
                    </div>
                ))}
            </div>
            <div className="navigation-buttons">
                <Link to={'/newbalance'} className="button-link">New Balance</Link>
                <Link to={'/'} className="button-link">Return</Link>
                <button onClick={logout}>Logout</button>
            </div>
        </div>
    );

};

export default BalanceManager;
